import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Home = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3000/recipes')
      .then(response => setRecipes(response.data))
      .catch(error => console.error('Error fetching recipes:', error));
  }, []);

  return (
    <div>
      <h1>foodieshare</h1>
      <h2>Welcome to foodieshare</h2>
      <h3>We Have a lot of Recipes</h3>

      {/* Links as buttons for navigation */}
      <Link to="/recipe-details">
        <button style={{ fontSize: '1.2em', margin: '10px' }}>Recipes Detail</button>
      </Link>
      <Link to="/recipe-list">
        <button style={{ fontSize: '1.1em', margin: '10px' }}>Recipe List</button>
      </Link>

      <ul>
        {recipes.map(recipe => (
          <li key={recipe._id}>
            <Link to={`/recipe/${recipe._id}`}>
              <h2>{recipe.title}</h2>
            </Link>
            <p><strong>Description:</strong> {recipe.description}</p>
            <p><strong>Ingredients:</strong> {recipe.ingredients}</p>
            <p><strong>Steps:</strong> {recipe.steps}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
